var asAlertMsg;
!function(){
    class t {
        constructor(t) {
            this.opt = t;
        }
        get starting() {
            return this.asAlertMsg_Starting();
        }
        asAlertMsg_Starting() {
            // Kiểm tra nếu được gọi với 'close' => Xóa alert
            if (this.opt === "close") {
                var existingAlert = document.getElementById("as-alert-holder");
                if (existingAlert) {
                    existingAlert.remove();
                }
                return;
            }

            var e = document.body,
                n = document.createElement("div");
            n.classList.add("as-alert-holder"), n.id = "as-alert-holder";

            var t = this.opt.message || "Thông báo!",
                s = this.opt.type || "default",
                o = this.opt.position || "middle-center",
                i = this.opt.timer || 3000;

            var alertHtml = `
                <div class="as-alert ${s} ${o} scale-up-center">
                    <div class="as-alert-box">
                        <div class="as-alert-content">
                            <div class="as-alert-title">${s.toUpperCase()}</div>
                            <div class="as-alert-message">${t}</div>
                            <div class="as-alert-box-button">
                                <button type="button" onclick="asAlertMsg('close')" class="as-alert-button-${s}">Đóng</button>
                            </div>
                        </div>
                    </div>
                </div>
            `;

            n.innerHTML = alertHtml;
            e.appendChild(n);

            setTimeout(() => {
                asAlertMsg("close");
            }, i);
        }
    }

    asAlertMsg = (e => {
        new t(e || "").starting();
    });

}();
